export interface CartMetafield {
    key: string;
    value: string;
}
export interface CheckoutMetafield {
    key: string;
    value: string;
}
export interface CartItem {
    cartItemId: string;
    variantId: string;
    quantity: number;
    metafields: CartMetafield[];
}
export interface Cart {
    cartItems: CartItem[];
    checkoutId: string;
    discountCodes?: string[];
    source?: string;
}
export interface Checkout {
    id: string;
    url: string;
    completed: boolean;
    source: string;
}
