export default ({ type, globalHandle, payload }) => {
  let path = ''

  if (globalHandle.indexOf('::') > -1) {
    globalHandle = globalHandle.replace(/::/gm, '--')
  }

  switch (type) {
    case 'page':
      path = 'pages/'
      break
    case 'collection':
      path = 'collections/'
      break
    case 'blog':
      path = 'blogs/'
      break
    case 'product':
      path = 'products/'
      break
    case 'article':
      if (payload && payload.blogHandle) {
        path = `articles/${payload.blogHandle}/`
      } else {
        path = `articles/blog/`
      }

      break
    case 'shop':
      path = ''
      break
    default:
      path = 'content/'
  }

  return `${path}${globalHandle}`
}
