import axios from 'axios'

export default async (message, error) => {
  console.log(message)
  console.log(error)

  const errorStr = Array.isArray(error) ? JSON.stringify(error) : error
  const text = `Build failed for space ${process.env.NACELLE_SPACE_ID}. ${errorStr}`

  await axios.post(
    'https://b3mfz9hvcl.execute-api.us-east-1.amazonaws.com/dev/build-alert',
    {
      text
    }
  )

  process.exit(1)
}
