const registerAttributionEvents = (nacelleClient) => {
  nacelleClient.events.onAttributionEvent('productRecommendation', (event) => ({
    sourceHandles: event.sourceHandles,
    recommendedHandles: event.recommendedHandles,
    clickedHandle: event.clickedHandle
  }))
  nacelleClient.events.onAttributionEvent('searchSelected', (event) => ({
    query: event.query,
    resultCount: event.resultCount
  }))
}

module.exports = { registerAttributionEvents }
