import fs from 'fs-extra'

export default dir => {
  // Clear out the old files
  fs.emptyDirSync(dir)

  // Make directories for each type of data
  fs.mkdirSync(`${dir}/collections`)
  fs.mkdirSync(`${dir}/pages`)
  fs.mkdirSync(`${dir}/products`)
  fs.mkdirSync(`${dir}/blogs`)
  fs.mkdirSync(`${dir}/articles`)
  fs.mkdirSync(`${dir}/content`)
}
